import api.vk.VKApiClient
import keyboards.MainKeyboardAfterPayment
import keyboards.MainKeyboardBeforePayment

suspend fun temporaryUpdate(clientsRepository: ClientsRepository, vkApiClient: VKApiClient) {
    val text = "–ë–æ–ª—å—à–æ–µ –æ–±–Ω–æ–≤–ª–µ–Ω–∏–µ: –Ω–æ–≤—ã–µ —Ñ—Ä–∞–∑—ã, —Ä–∞–∑–¥–µ–ª —Å –º–æ—Ç–∏–≤–∞—Ü–∏–µ–π –∏ –æ—Ü–µ–Ω–∫–∞ —Ç—Ä–µ–Ω–∏—Ä–æ–≤–æ—á–Ω—ã—Ö –ø–ª–∞–Ω–æ–≤!\n\n" +
            "–ù–æ–≤—ã–µ —Ñ—Ä–∞–∑—ã üî•\n–¢–µ–ø–µ—Ä—å —á–∞—Ç-–±–æ—Ç —Å—Ç–∞–ª –æ—Ç–≤–µ—á–∞—Ç—å –ø–æ–Ω—è—Ç–Ω–µ–µ –∏ —Ä–∞–∑–Ω–æ–æ–±—Ä–∞–∑–Ω–µ–µ. –≠—Ç–æ –¥–µ–ª–∞–µ—Ç –≤–∑–∞–∏–º–æ–¥–µ–π—Å—Ç–≤–∏–µ —Å –Ω–∏–º –ø—Ä–æ—â–µ –∏ –∏–Ω—Ç–µ—Ä–µ—Å–Ω–µ–µ!\n\n" +
            "–†–∞–∑–¥–µ–ª —Å –º–æ—Ç–∏–≤–∞—Ü–∏–µ–π üèÜ\n–ö —Ä–∞–∑–¥–µ–ª—É —Å —ç–∫—Å–∫–ª—é–∑–∏–≤–Ω—ã–º–∏ –ø—Ä–æ–º–æ–∫–æ–¥–∞–º–∏ —Ç–µ–ø–µ—Ä—å –¥–æ–±–∞–≤–∏–ª–∞—Å—å —Å—Ç–∞—Ç—å—è, –≤ –∫–æ—Ç–æ—Ä–æ–π —Å–æ–±—Ä–∞–Ω—ã –ª—É—á—à–µ–µ –º–æ—Ç–∏–≤–∏—Ä—É—é—â–∏–µ —Ñ–∏–ª—å–º—ã. –î–æ—Å—Ç—É–ø–Ω–æ –ø–æ—Å–ª–µ –ø–µ—Ä–≤–æ–π –æ–ø–ª–∞—Ç—ã.\n\n" +
            "–í–æ–∑–º–æ–∂–Ω–æ—Å—Ç—å –æ—Ü–µ–Ω–∏–≤–∞—Ç—å —Ç—Ä–µ–Ω–∏—Ä–æ–≤–æ—á–Ω—ã–µ –ø–ª–∞–Ω—ã ü§©\n–ü–æ –æ–∫–æ–Ω—á–∞–Ω–∏–∏ —Ç—Ä–µ–Ω–∏—Ä–æ–≤–æ—á–Ω–æ–≥–æ —Ü–∏–∫–ª–∞ –í–∞–º –±—É–¥–µ—Ç –ø—Ä–µ–¥–ª–æ–∂–µ–Ω–æ –æ—Ü–µ–Ω–∏—Ç—å –µ–≥–æ –ø–æ 5-–±–∞–ª—å–Ω–æ–π —à–∫–∞–ª–µ. –í—Å–µ –í–∞—à–∏ –æ—Ü–µ–Ω–∫–∏ –±—É–¥—É—Ç —É—á–∏—Ç—ã–≤–∞—Ç—å—Å—è –ø—Ä–∏ —Ñ–æ—Ä–º–∏—Ä–æ–≤–∞–Ω–∏–∏ —Å–ª–µ–¥—É—é—â–∏—Ö –ø–ª–∞–Ω–æ–≤.\n\n" +
            "–û–ø—Ä–æ–±—É–π—Ç–µ –≤—Å–µ —ç—Ç–æ –≤ –¥–µ–π—Å—Ç–≤–∏–∏, –µ—Å–ª–∏ –í—ã —É–∂–µ —Ç—Ä–µ–Ω–∏—Ä—É–µ—Ç–µ—Å—å —Å –Ω–∞–º–∏! –ê –µ—Å–ª–∏ –í—ã –æ—Å—Ç–∞–Ω–æ–≤–∏–ª–∏—Å—å –Ω–∞ –ø—Ä–æ–±–Ω–æ–π –Ω–µ–¥–µ–ª–µ, —Ç–æ –Ω–µ –±–æ–π—Ç–µ—Å—å –∏–¥—Ç–∏ –¥–∞–ª—å—à–µ!"
    clientsRepository.getAll().forEach {
        vkApiClient.sendMessageSafely(
            it.id,
            text,
            keyboard = if (!it.trial) MainKeyboardAfterPayment().keyboard else MainKeyboardBeforePayment().keyboard
        )
    }
}